<table class="user_table">	<?php if(sizeof($pms)>0):?>	<thead>		<tr>						<td>title</td>			<td width="100"> <?php if($type=="inbox"):?>From<?php else:?>To<?php endif;?></td>			<td width="150">Time</td>			<td width="40">Action</td>					</tr>	</thead>	<tbody>		<?php foreach($pms as $pm):?>		<tr>			<td><a href="<?=site_url('/pm/show/'.$pm->id)?>" <?php if($type=="inbox"&&$pm->unread):?>class="unread"<?php endif;?>><?=htmlspecialchars($pm->title)?></a></td>			<td><a href="<?=site_url('member/'.$pm->username)?>"><?=$pm->username?></a></td>			<td><span class="time"><?=time_format($pm->created_at)?></span></td>			<td width="40" class="del"><a href="<?=site_url("/pm/del/".$pm->id)?>">X</a></td>		</tr>		<?php endforeach;?>				 <tbody>	 <?php else:?>	 <div class="box">No message in this box</div>	<?php endif;?>		</table><?=$pagination_links?>